create table product (version integer, concept_id bigint, created timestamp(6) with time zone not null, id bigint generated by default as identity, modified timestamp(6) with time zone, ticket_id bigint not null, name varchar(2048) not null, created_by varchar(255), modified_by varchar(255), package_details json not null, primary key (id), constraint product_name_ticket_unique unique (ticket_id, name));
create table product_aud (rev integer not null, revtype smallint, concept_id bigint, id bigint not null, ticket_id bigint, name varchar(2048), package_details json, primary key (rev, id));
alter table if exists product add constraint FKmq9708kyj1x47rd6dlqu1f8hv foreign key (ticket_id) references ticket;
alter table if exists product_aud add constraint FK9vwllld6jlw5xys1ay911oh1x foreign key (rev) references revinfo;
